mapper.sql:
FROM TB_QNA
WHERE DEL_YN = 'N'
	
		AND REG_USER = #{username}

mapper.insert:

	SELECT IFNULL(MAX(QNA_ID), 0) + 1 FROM TB_QNA 

INSERT INTO TB_QNA (
	QNA_ID
	, PARENT_ID
	, TITLE
	, CONTENT
	, REG_USER
) VALUES (
	#{qnaId}
	, #{parentId}
	, #{title}
	, #{content}
	, #{regUser}
)

mapper.update:
UPDATE TB_QNA SET
	UDT_DT = CURRENT_TIMESTAMP
	, TITLE = #{title}
	, CONTENT = #{content}
WHERE QNA_ID = #{qnaId}

UPDATE TB_QNA SET
	REPLY_DT = CURRENT_TIMESTAMP
	, REPLY_USER = #{replyUser}
	, REPLY = #{reply}
WHERE QNA_ID = #{qnaId}
